import firebase from 'firebase/app';

import 'firebase/analytics';
import 'firebase/auth';
import 'firebase/firestore';

const firebaseConfig = {
    apiKey: "AIzaSyBqJzv85KtxPZNGgvfrP-aUinnPoCEr1PY",
    authDomain: "hybrid-chat-app.firebaseapp.com",
    projectId: "hybrid-chat-app",
    storageBucket: "hybrid-chat-app.appspot.com",
    messagingSenderId: "603736836187",
    appId: "1:603736836187:web:d1338c46c40ad4f452977e",
    measurementId: "G-C1YYFWYG8G"
};

// Initialize Firebase
const app = firebase.initializeApp(firebaseConfig);
const analytics = firebase.getAnalytics(app);


const auth = firebase.auth();
const db = firebase.firestore();

export { db, auth };
export default firebase;
